mapper.select:
SELECT operation_log_id AS operationLogId,
       operation_time   AS operationTime,
       user_id             userId,
       username,
       message
FROM operation_log
WHERE operation_time >= #{start}
  AND operation_time <= #{end}
ORDER BY operation_time DESC
LIMIT #{p.offset}, #{p.pageSize}

SELECT COUNT(1)
FROM operation_log
WHERE operation_time >= #{start}
  AND operation_time <= #{end}
