mapper.select:
SELECT operation_log_id AS operationLogId,
       operation_time   AS operationTime,
       user_id             userId,
       username,
       message
FROM operation_log
WHERE operation_time >= #{startTime}
  AND operation_time <= #{endTime}
ORDER BY operation_time DESC
LIMIT #{page.offset}, #{page.pageSize}

SELECT COUNT(1)
FROM operation_log
WHERE operation_time >= #{startTime}
  AND operation_time <= #{endTime}
