mapper.select:
SELECT user_id AS userId, STRFTIME('%Y-%m-%d %H:%M:%S', modify_time) AS modifyTime, is_enabled AS enabled, username, IFNULL(roles, '') AS roles FROM user
username LIKE #{username}
ORDER BY modify_time DESC LIMIT #{page.offset}, #{page.pageSize}

mapper.update:
UPDATE user

    modify_time = DATETIME(CURRENT_TIMESTAMP, 'localtime'),

        hashed_password = #{hashedPassword},

    roles = #{roles}

WHERE user_id = #{userId}

UPDATE user
SET modify_time     = DATETIME(CURRENT_TIMESTAMP, 'localtime'),
    hashed_password = #{hashedPassword}
WHERE user_id = #{userId}
