mapper.select:
SELECT user_id, STRFTIME('%Y-%m-%d %H:%M:%S', modify_time) AS modifyTime, username, IFNULL(roles, '') AS roles FROM user
username LIKE #{username}
ORDER BY modify_time DESC LIMIT #{page.offset}, #{page.pageSize}

mapper.insert:
INSERT INTO user (
  create_time,
  modify_time,
  username, hashed_password, roles
)
VALUES (
  DATETIME(CURRENT_TIMESTAMP, 'localtime'),
  DATETIME(CURRENT_TIMESTAMP, 'localtime'),
  #{username}, #{password}, #{roles}
)

mapper.update:
UPDATE user

  modify_time = DATETIME(CURRENT_TIMESTAMP, 'localtime'),
  hashed_password = #{password},
  roles = #{roles}

WHERE user_id = #{userId}

UPDATE user
SET
  modify_time = DATETIME(CURRENT_TIMESTAMP, 'localtime'),
  hashed_password = #{password}
WHERE user_id = #{userId}
